import { useSelector } from "react-redux";
import {motion} from 'framer-motion'
import styles from "./Inicio.module.css"

function Inicio() {

  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  const containerVariants={
    hidden:{y:-2000},
    visible:{y:0,
    transition:{
        duration:1.5
    }}
}

  return (
    <div id="inicio" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
        <motion.div
        className={styles.dataContainer}
        initial="hidden"
        animate="visible"
        variants={containerVariants}>
            <p className={`${styles.welcomeLigth} ${temaClaro ? '' : styles.welcomeDark}`}>{idioma ? "Welcome!" : "Bienvenido!"}</p>
            <h1 className={`${styles.nameLigth} ${temaClaro ? '' : styles.nameDark}`}>Zalduendo Javier</h1>
            <h2 className={`${styles.titleLigth} ${temaClaro ? '' : styles.titleDark}`}>
              {idioma ? "FullStack Developer" : "Desarrollador Web FullStack"}
            </h2>
            <p className={`${styles.textLigth} ${temaClaro ? '' : styles.nameDark}`}>
              {idioma === 'ES' && (" Soy un colaborador entusiasta y siempre estoy ansioso por aprender y enfrentar nuevos desafíos. Me gusta trabajar en equipo,compartiendo ideas y conocimientos para lograr buenos resultados. Mi objetivo es seguir creciendo profesionalmente, aprendiendo nuevas tecnologías y mejores prácticas para desarrollar soluciones que impacten positivamente en la vida de las personas.")}
              {idioma === 'EN' && ("I'm a FullStack Developer and my main objetive is to improve the IT world through creative and efficient solutions. I'm teamworker, i like to share new ideas and knowledge in order to improve and help on my current proyect")}
            </p>
             <a className={styles.fancy} href="https://drive.google.com/file/d/1C8FE-_k4yALmmQnFKWEtNxdRahr6QUiN/view?usp=drive_link" target="_blank" rel="noopener noreferrer">
              <span className={styles.topKey}></span>
              <span className={styles.text}>Curriculum</span>
              <span className={styles.bottomKey1}></span>
              <span className={styles.bottomKey2}></span>
            </a>
            <div className={`${styles.scrolldown} ${temaClaro ? '' : styles.scrolldownDark}`}>
                <div className={`${styles.chevrons} ${temaClaro ? '' : styles.chevronsDark}`}>
                    <div className={`${styles.chevrondown} ${temaClaro ? '' : styles.chevrondownDark}`}></div>
                    <div className={`${styles.chevrondown} ${temaClaro ? '' : styles.chevrondownDark}`}></div>
                </div>
            </div>
        </motion.div>
    </div>
  );
}

export default Inicio;
