import React, { useState } from "react";
import { useSelector } from "react-redux";
import { motion } from 'framer-motion';
import styles from "./Contact.module.css";

function Contacto() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  const [input, setInput] = useState({
    name: "",
    lastName: "",
    email: "",
    message: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInput({
      ...input,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div id="Contacto" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
      <motion.div className={styles.dataContainer}>

        <div className={`${styles.title} ${temaClaro ? '' : styles.titleDark}`}>
          {idioma === 'ES' && <h2>CONTACTO</h2>}
          {idioma === 'EN' && <h2>CONTACT</h2>}
        </div>

          <div classname={form-container}>
                <div classname={form}>
                    <span class="heading">Get in touch</span>
                    <input placeholder="Name" type="text" class="input"/>
                    <input placeholder="Email" id="mail" type="email" class="input"/>
                    <textarea placeholder="Say Hello" rows="10" cols="30" id="message" name="message" class="textarea"></textarea>
                    <div class="button-container">
                    <div class="send-button">Send</div>
                    <div class="reset-button-container">
                        <div id="reset-btn" class="reset-button">Reset</div>
                    </div>
                </div>
            </div>
        </div>
      </motion.div>
    </div>
  );
}

export default Contacto;
