import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { motion } from 'framer-motion';
import styles from './Inicio.module.css';

function Inicio() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  // Define el texto original y el estado para el tipeo
  const textoOriginal = idioma === 'ES' ? 'HOLA, SOY JAVIER ZALDUENDO' : "HEY, I'M JAVIER ZALDUENDO";
  const [textoTipeado, setTextoTipeado] = useState('');

  useEffect(() => {
    // Función para simular el tipeo
    const tipearTexto = () => {
      for (let i = 0; i <= textoOriginal.length; i++) {
        setTimeout(() => {
          setTextoTipeado(textoOriginal.substring(0, i));
        }, 100 * i); // Cambia la velocidad de tipeo ajustando este valor
      }
    };

    tipearTexto();
  }, [textoOriginal]);

  return (
    <div id="inicio" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
      <motion.div className={styles.dataContainer}>
        <h1 className={`${styles.titleLight} ${temaClaro ? '' : styles.titleDark}`}>{textoTipeado}</h1>
        {/* Resto de tu contenido */}
        <p className={`${styles.textLight} ${temaClaro ? '' : styles.textDark}`}>
          {idioma === 'ES' &&
            'Un Desarrollador Web Full Stack apasionado por la creación de soluciones digitales. Mi formación en desarrollo web y marketing me ha brindado las habilidades necesarias para construir sitios web y aplicaciones impactantes. Siempre estoy buscando nuevos desafíos y en constante aprendizaje para ofrecer productos de alta calidad.'}
          {idioma === 'EN' &&
            "A passionate Full Stack Web Developer dedicated to crafting digital solutions. My education in web development and marketing has equipped me with the skills needed to build impactful websites and applications. I'm always seeking new challenges and continuously learning to deliver high-quality products."}
        </p>
        <button className={styles.botonProyectos}>
          {idioma === 'ES' && 'PROYECTOS'}
          {idioma === 'EN' && 'PROJECTS'}
        </button>
        <div className={styles.scrollPosition}>
          <div className={`${styles.scrolldown} ${temaClaro ? '' : styles.scrolldownDark}`}>
            <div className={`${styles.chevrons} ${temaClaro ? '' : styles.chevronsDark}`}>
              <div className={`${styles.chevrondown} ${temaClaro ? '' : styles.chevrondownDark}`}></div>
              <div className={`${styles.chevrondown} ${temaClaro ? '' : styles.chevrondownDark}`}></div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
}

export default Inicio;
