import React from "react";
import { useSelector } from "react-redux";
import styles from "./RightSideBar.module.css";

function RightSideBar() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  // URL del enlace al currículum
  const curriculumURL =
    "https://drive.google.com/file/d/1C8FE-_k4yALmmQnFKWEtNxdRahr6QUiN/view?usp=drive_link";

  return (
    <div>
      {/* Utilizamos una etiqueta 'a' para el enlace */}
      <a
        href={curriculumURL}
        className={`${styles.buttonCV} ${temaClaro ? '' : styles.buttonCVDark}`}
        target="_blank" // Abre el enlace en una nueva pestaña
        rel="noopener noreferrer" // Buenas prácticas para la seguridad
      >
        <span className={`${styles.buttonCV__text} ${temaClaro ? '' : styles.buttonCV__textDark}`}>
          Curriculum
        </span>
            <span className={`${styles.buttonCV__icon} ${temaClaro ? '' : styles.buttonCV__iconDark}`}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 35 35" id="bdd05811-e15d-428c-bb53-8661459f9307" data-name="Layer 2" className={styles.svg}>
                <path d="M17.5,22.131a1.249,1.249,0,0,1-1.25-1.25V2.187a1.25,1.25,0,0,1,2.5,0V20.881A1.25,1.25,0,0,1,17.5,22.131Z"></path>
                <path d="M17.5,22.693a3.189,3.189,0,0,1-2.262-.936L8.487,15.006a1.249,1.249,0,0,1,1.767-1.767l6.751,6.751a.7.7,0,0,0,.99,0l6.751-6.751a1.25,1.25,0,0,1,1.768,1.767l-6.752,6.751A3.191,3.191,0,0,1,17.5,22.693Z"></path><path d="M31.436,34.063H3.564A3.318,3.318,0,0,1,.25,30.749V22.011a1.25,1.25,0,0,1,2.5,0v8.738a.815.815,0,0,0,.814.814H31.436a.815.815,0,0,0,.814-.814V22.011a1.25,1.25,0,1,1,2.5,0v8.738A3.318,3.318,0,0,1,31.436,34.063Z"></path>
              </svg>
            </span>
          </a>
    </div>
  );
}

export default RightSideBar;
