import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { motion } from "framer-motion";
import styles from "./Inicio.module.css";

function Inicio() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  // Define el estado local para el texto del título
  const [titulo, setTitulo] = useState(
    idioma === "ES" ? "HOLA, SOY JAVIER ZALDUENDO" : "HEY, I'M JAVIER ZALDUENDO"
  );

  // Define una función para animar el texto del título
  const animateTitulo = () => {
    setTimeout(() => {
      setTitulo(
        idioma === "ES" ? "DESARROLLADOR WEB" : "FULL STACK WEB DEVELOPER"
      );
    }, 2000); // Cambia el texto después de 2 segundos
  };

  // Utiliza useEffect para activar la animación cuando el componente se monta
  useEffect(() => {
    animateTitulo();
  }, []);

  return (
    <div
      id="inicio"
      className={`${styles.containerLight} ${
        temaClaro ? "" : styles.containerDark
      }`}
    >
      <motion.div className={styles.dataContainer}>
        {/* Utiliza el estado local para el título */}
        <h1
          className={`${styles.titleLight} ${
            temaClaro ? "" : styles.titleDark
          }`}
        >
          {titulo}
        </h1>
        <p className={`${styles.textLight} ${temaClaro ? "" : styles.textDark}`}>
          {idioma === "ES"
            ? "Un Desarrollador Web Full Stack apasionado por la creación de soluciones digitales. Mi formación en desarrollo web y marketing me ha brindado las habilidades necesarias para construir sitios web y aplicaciones impactantes. Siempre estoy buscando nuevos desafíos y en constante aprendizaje para ofrecer productos de alta calidad."
            : "A passionate Full Stack Web Developer dedicated to crafting digital solutions. My education in web development and marketing has equipped me with the skills needed to build impactful websites and applications. I'm always seeking new challenges and continuously learning to deliver high-quality products."}
        </p>
        <button className={styles.botonProyectos}>
          {idioma === "ES" ? "PROYECTOS" : "PROJECTS"}
        </button>
        <div className={styles.scrollPosition}>
          <div
            className={`${styles.scrolldown} ${
              temaClaro ? "" : styles.scrolldownDark
            }`}
          >
            <div
              className={`${styles.chevrons} ${
                temaClaro ? "" : styles.chevronsDark
              }`}
            >
              <div
                className={`${styles.chevrondown} ${
                  temaClaro ? "" : styles.chevrondownDark
                }`}
              ></div>
              <div
                className={`${styles.chevrondown} ${
                  temaClaro ? "" : styles.chevrondownDark
                }`}
              ></div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
}

export default Inicio;
