import React from 'react';
import styles from '../NavBar/NavBar.module.css';
import LogoJZNegro from '../../Assets/LogoJZNegro.png';

import { useDispatch, useSelector } from 'react-redux';
import { cambioIDioma, cambioTema } from '../../Redux/actions';

function Navbar() {
  const dispatch = useDispatch();
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro); // Nuevo estado para el tema

  const handleIdiomaChange = () => {
    dispatch(cambioIDioma());
  };

  const handleTemaChange = () => { // Función para cambiar el tema
    dispatch(cambioTema());
  };

  return (
    <div className={styles.navbar}>
      
      <img className={styles.logo} src={LogoJZNegro} alt="logo" />
      
      <div className={styles.sections}>
      <ul>
          {idioma === 'ES' && (
            <>
              <li>Inicio</li>
              <li>Sobre mí</li>
              <li>Habilidades</li>
              <li>Proyectos</li>
              <li>Contacto</li>
            </>
          )}
          {idioma === 'EN' && (
            <>
              <li>Home</li>
              <li>About me</li>
              <li>Skills</li>
              <li>Projects</li>
              <li>Contact</li>
            </>
          )}
        </ul>
      </div>
      <div className={styles.Theme}>
        <div>
          <label className={styles.switch}>
            <input type="checkbox" onChange={handleTemaChange} checked={!temaClaro} />
            <span className={styles.slider}></span>
          </label>
        </div>
        <div className={styles.Idioma}>
          <button className={idioma === "ES" ? styles.active : ''} onClick={handleIdiomaChange}>ES</button>
          <button className={idioma === "EN" ? styles.active : ''} onClick={handleIdiomaChange}>EN</button>
        </div>
      </div>
    </div>
  );
}

export default Navbar;
