import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import styles from "./RightSideBar.module.css"

function RightSideBar() {

  const temaClaro = useSelector((state) => state.temaClaro);

  const curriculumURL ="https://drive.google.com/drive/folders/1JtqeghtQKtKru50huRBvgKFOOk1-Ma2-?usp=sharing";

  const handleButtonClick = () => {
    window.open(curriculumURL, "_blank");
  };

  useEffect(() => {
    // Obtén la referencia al elemento del footer
    const footer = document.getElementById("footer");

    // Escucha el evento de desplazamiento
    window.addEventListener("scroll", () => {
      // Obtén la posición del footer en relación con la ventana del navegador
      const footerRect = footer.getBoundingClientRect();

      // Si el footer está visible en la ventana del navegador
      if (footerRect.top >= 0 && footerRect.bottom <= window.innerHeight) {
        // Bloquea el desplazamiento adicional
        document.body.style.overflow = "hidden";
      } else {
        // Habilita el desplazamiento si no estás en la sección del footer
        document.body.style.overflow = "auto";
      }
    });

    // Limpia el evento de desplazamiento al desmontar el componente
    return () => {
      window.removeEventListener("scroll", () => {});
    };
  }, []);

  return (
    <div>
          <button className={`${styles.buttonCV} ${temaClaro ? '' : styles.buttonCVDark}`} type="button" onClick={handleButtonClick}>
            <span className={`${styles.buttonCV__text} ${temaClaro ? '' : styles.buttonCV__textDark}`}>Curriculum</span>
            <span className={`${styles.buttonCV__icon} ${temaClaro ? '' : styles.buttonCV__iconDark}`}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 35 35" id="bdd05811-e15d-428c-bb53-8661459f9307" data-name="Layer 2" className={styles.svg}>
                <path d="M17.5,22.131a1.249,1.249,0,0,1-1.25-1.25V2.187a1.25,1.25,0,0,1,2.5,0V20.881A1.25,1.25,0,0,1,17.5,22.131Z"></path>
                <path d="M17.5,22.693a3.189,3.189,0,0,1-2.262-.936L8.487,15.006a1.249,1.249,0,0,1,1.767-1.767l6.751,6.751a.7.7,0,0,0,.99,0l6.751-6.751a1.25,1.25,0,0,1,1.768,1.767l-6.752,6.751A3.191,3.191,0,0,1,17.5,22.693Z"></path><path d="M31.436,34.063H3.564A3.318,3.318,0,0,1,.25,30.749V22.011a1.25,1.25,0,0,1,2.5,0v8.738a.815.815,0,0,0,.814.814H31.436a.815.815,0,0,0,.814-.814V22.011a1.25,1.25,0,1,1,2.5,0v8.738A3.318,3.318,0,0,1,31.436,34.063Z"></path>
              </svg>
            </span>
          </button>
    </div>
  );
}

export default RightSideBar;
