import React from "react";
import { useSelector } from "react-redux";
import { motion, useAnimation } from "framer-motion";
import styles from "./Footer.module.css";
import LogoJZNegro from "../../Assets/LogoJZNegro.png";
import LogoJZBlanco from "../../Assets/LogoJZBlanco.png";

function Footer() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  const controls = useAnimation();

  const scrollToInicio = async () => {
    const destino = document.getElementById("inicio");
    if (destino) {
      await controls.start({ y: destino.offsetTop });
      window.scrollTo({ top: destino.offsetTop, behavior: "smooth" });
    }
  };

  const logoImage = temaClaro ? LogoJZNegro : LogoJZBlanco;

  return (
    <div id="footer" className={`${styles.containerLight} ${temaClaro ? "" : styles.containerDark}`}>
      <div className={styles.footerContent}>
        <div className={styles.logoContainer}>
          <img src={logoImage} alt="Logo" />
        </div>
        <div className={styles.aboutMeContainer}>
          <h3>Javier Zalduendo</h3>
          <p>
            Un Desarrollador Web Full Stack apasionado por la creación de soluciones digitales. Mi formación en desarrollo web y marketing me ha brindado las habilidades necesarias para construir sitios web y aplicaciones impactantes. Siempre estoy buscando nuevos desafíos y en constante aprendizaje para ofrecer productos de alta calidad.
          </p>
        </div>
        <div className={styles.textContainer}>
          <p>© Copyright 2023. Made by Javier Zalduendo</p>
        </div>
        <div className={styles.socialContainer}>
          <a href="https://www.linkedin.com/" target="_blank" rel="noopener noreferrer">
          </a>
          <a href="https://github.com/" target="_blank" rel="noopener noreferrer">
          </a>
          <a href="https://www.instagram.com/" target="_blank" rel="noopener noreferrer">
          </a>
          <a href="https://example.com/your-cv.pdf" target="_blank" rel="noopener noreferrer">
            <img alt="CV" />
          </a>
        </div>
      </div>
    </div>
  );
}

export default Footer;
