import React from "react";
import { useSelector } from "react-redux";
import {motion} from 'framer-motion'
import styles from "./AboutMe.module.css"

function AboutMe() {

  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  return (
    <div id="sobreMi" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
        <motion.div className={styles.container}>
            <div className={styles.title}>
                {idioma === 'ES' && <h2>Sobre Mi</h2>}
                {idioma === 'EN' && <h2>About Me</h2>}
            </div>
            <div className={styles.content}>
          
          <div className={styles.paragraph}>
            <p>
              {idioma === 'ES' && "¡Hola! Soy Javier Zalduendo, un apasionado Desarrollador Web Full Stack, entusiasta del Marketing y admirador de la Inteligencia Artificial. Mi trayecto educativo me ha llevado a formarme en el desarrollo web en Henry, al mismo tiempo que curso una Licenciatura en Marketing en la Universidad Nacional de Tres de Febrero. Me apasiona la creación de soluciones digitales y el desarrollo de experiencias web que impacten positivamente en la vida de las personas. A lo largo de mi formación, he adquirido habilidades en diversas tecnologías, incluyendo JavaScript, React, HTML, CSS, Redux, Node.js, Express, Sequelize, PostgreSQL, SEO y SEM. Estas habilidades me han permitido desarrollar proyectos sólidos y funcionales que marcan la diferencia. Además, tengo experiencia como Community Manager y Web Manager, donde me dedico a crear estrategias de publicidad online y offline para aumentar la presencia de la marca en el mercado, así como al mantenimiento de la página web empresarial. Esta experiencia me ha permitido comprender la importancia de una comunicación efectiva y el impacto que puede tener en el crecimiento y éxito de un negocio. Mi enfoque en el aprendizaje continuo me impulsa a mantenerme actualizado con las últimas tendencias en desarrollo web y marketing. Me encanta enfrentar nuevos desafíos y resolver problemas de manera creativa, siempre buscando superar mis propios límites. Creo firmemente en el poder del trabajo en equipo y la colaboración, valores que aprendí como jugador de rugby durante 15 años. Mi objetivo es seguir creciendo profesionalmente, contribuyendo con mis habilidades y conocimientos a proyectos significativos que generen un impacto positivo en la sociedad. Cuando no estoy trabajando en proyectos web o estudiando, disfruto de hacer deporte y estar con familiares y amigos."}
              {idioma === 'EN' && "Hello! I'm [Your Name], a passionate web developer with experience in..."}
            </p>
        
            <a href="#contacto" className={styles.contactButton}>
              {idioma === 'ES' && "Contáctame"}
              {idioma === 'EN' && "Contact Me"}
            </a>
          </div>

          <div className={styles.skills}>
            <div className={styles.skillItem}>
              <img src="/images/html.png" alt="HTML" />
            </div>
            <div className={styles.skillItem}>
              <img src="/images/css.png" alt="CSS" />
            </div>
          </div>
        </div>
        </motion.div>
    </div>
  );
}

export default AboutMe;
