import React, { useState } from "react";
import { useSelector } from "react-redux";
import { motion } from 'framer-motion';
import styles from "./Contact.module.css";

function Contacto() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  const [input, setInput] = useState({
    name: "",
    lastName: "",
    email: "",
    message: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInput({
      ...input,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div id="Contacto" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
      <motion.div className={styles.dataContainer}>
        
        <div className={`${styles.title} ${temaClaro ? '' : styles.titleDark}`}>
          {idioma === 'ES' && <h2>CONTACTO</h2>}
          {idioma === 'EN' && <h2>CONTACT</h2>}
        </div>

        <div className={`${styles.contacto} ${temaClaro ? '' : styles.contactoDark}`}>
          <form onSubmit={handleSubmit}>
            <div className={`${styles.formGroup} ${temaClaro ? '' : styles.formGroupDark}`}>
              <label htmlFor="name">Nombre:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={input.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className={`${styles.formGroup} ${temaClaro ? '' : styles.formGroupDark}`}>
              <label htmlFor="lastName">Apellido:</label>
              <input
                type="text"
                id="lastName"
                name="lastName"
                value={input.lastName}
                onChange={handleChange}
                required
              />
            </div>
            <div className={`${styles.formGroup} ${temaClaro ? '' : styles.formGroupDark}`}>
              <label htmlFor="email">Correo Electr√≥nico:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={input.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className={`${styles.formGroup} ${temaClaro ? '' : styles.formGroupDark}`}>
              <label htmlFor="message">Mensaje:</label>
              <textarea
                id="message"
                name="message"
                value={input.message}
                onChange={handleChange}
                required
              ></textarea>
            </div>
            <button type="submit">Enviar</button>
          </form>
        </div>
      </motion.div>
    </div>
  );
}

export default Contacto;
