import { CAMBIO_IDIOMA } from "./actions";

let initialState = {
  isEnglish: false,
  idioma: "ES",
  temaClaro: true,
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case CAMBIO_IDIOMA:
      return {
        ...state,
        isEnglish: !state.isEnglish,
        idioma: state.isEnglish ? "ES" : "EN",
      };
    case CAMBIO_TEMA:
      return {
        ...state,
        temaClaro: !state.temaClaro,
      };
    default:
      return state;
  }
}
