import React from "react";
import { useSelector } from "react-redux";
import {motion} from 'framer-motion'
import styles from "./AboutMe.module.css"

function AboutMe() {

  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  return (
    <div id="sobreMi" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
        <motion.div className={styles.container}>
            <div className={styles.title}>
                {idioma === 'ES' && <h2>Sobre Mi</h2>}
                {idioma === 'EN' && <h2>About Me</h2>}
            </div>
            <div className={styles.content}>
          
          <div className={styles.paragraph}>
            <p>
            {idioma === 'ES' && (
            <p>¡Hola! Soy un <span style={{ fontWeight: 'bold', color: '#555' }}>Desarrollador Web Full Stack</span>. Mi objetivo es crear soluciones digitales impactantes que mejoren la vida de las personas. He trabajado como <strong>Community Manager y Web Manager</strong>, desarrollando estrategias de marketing y manteniendo sitios web empresariales. Soy un entusiasta del aprendizaje constante, siempre al tanto de las últimas tendencias en desarrollo web y marketing. Además, valoro la colaboración y el <strong>trabajo en equipo</strong>. Estoy listo para contribuir con mis habilidades y conocimientos a proyectos significativos.
            </p>)}
              {idioma === 'EN' && "Hello! I'm [Your Name], a passionate web developer with experience in..."}
            </p>
        
            <a href="#contacto" className={styles.contactButton}>
              {idioma === 'ES' && "Contáctame"}
              {idioma === 'EN' && "Contact Me"}
            </a>
          </div>

          <div className={styles.skills}>
            <div className={styles.skillItem}>
              <img src="/images/html.png" alt="HTML" />
            </div>
            <div className={styles.skillItem}>
              <img src="/images/css.png" alt="CSS" />
            </div>
          </div>
        </div>
        </motion.div>
    </div>
  );
}

export default AboutMe;
