import React from "react";
import { useSelector } from "react-redux";
import {motion} from 'framer-motion'
import styles from "./AboutMe.module.css"

function AboutMe() {

  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  return (
    <div id="sobreMi" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
        <motion.div className={styles.container}>
            <div className={styles.title}>
                {idioma === 'ES' && <h2>Sobre Mi</h2>}
                {idioma === 'EN' && <h2>About Me</h2>}
            </div>
            <div className={styles.content}>
          
          <div className={styles.paragraph}>
            <p>
            {idioma === 'ES' && (
            <p>Soy un <span style={{ fontWeight: 'bold', color: '#404040' }}>Desarrollador Web Full Stack</span> apasionado por crear soluciones digitales impactantes. Mi experiencia incluye proyectos diversos, así como roles de <span style={{ fontWeight: 'bold', color: '#404040' }}>Community Manager y Web Manager</span>, donde he desarrollado estrategias de marketing y gestionado sitios web empresariales.
            <br></br>
            <br></br>
            Mi capacidad de liderazgo, <span style={{ fontWeight: 'bold', color: '#404040' }}>trabajo en equipo</span> y creatividad son mis rasgos distintivos en el mundo del desarrollo. Estoy listo para contribuir a <span style={{ fontWeight: 'bold', color: '#404040' }}>proyectos</span> significativos y abierto a oportunidades laborales que me permitan aprender y crecer. Si tienes una oportunidad que coincida, no dudes en contactarme.
            </p>)}
            {idioma === 'EN' && (
            <p>I'm a <span style={{ fontWeight: 'bold', color: '#404040' }}>Full Stack Web Developer</span>. My goal is to create impactful digital solutions that enhance people's lives. I have experience as a <span style={{ fontWeight: 'bold', color: '#404040' }}>Community Manager and Web Manager</span>, developing marketing strategies and maintaining corporate websites. I'm an enthusiast of continuous learning, always staying updated with the latest trends in web development and marketing. Furthermore, I value collaboration and <span style={{ fontWeight: 'bold', color: '#404040' }}>teamwork</span>. I'm ready to contribute my skills and knowledge to meaningful projects.
            </p>)}
            
            </p>
            <a href="#contacto" className={styles.contactButton}>
              {idioma === 'ES' && "Contáctame"}
              {idioma === 'EN' && "Contact Me"}
            </a>
          </div>

          <div className={styles.skills}>
            <div className={styles.skillItem}>
              <img src="/images/html.png" alt="HTML" />
            </div>
            <div className={styles.skillItem}>
              <img src="/images/css.png" alt="CSS" />
            </div>
          </div>
        </div>
        </motion.div>
    </div>
  );
}

export default AboutMe;
