import React from "react";
import { useSelector } from "react-redux";
import { useAnimation } from "framer-motion";
import styles from "./Footer.module.css";

function Footer() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  const controls = useAnimation();

  const scrollToInicio = async () => {
    const destino = document.getElementById("inicio");
    if (destino) {
      await controls.start({ y: destino.offsetTop });
      window.scrollTo({ top: destino.offsetTop, behavior: "smooth" });
    }
  };

  return (
    <div id="footer" className={`${styles.containerLight} ${temaClaro ? "" : styles.containerDark}`}>
      <div className={styles.footerContent}>
        <div className={styles.aboutMeContainer}>
          <h3>Javier Zalduendo</h3>
          <p>
            Un Desarrollador Web Full Stack apasionado por crear soluciones digitales con experiencia en desarrollo web y marketing. Siempre en busca de nuevos desafíos para ofrecer productos de alta calidad.
          </p>
        </div>
        <div className={styles.textContainer}>
          <p>© Copyright 2023. Made by Javier Zalduendo</p>
        </div>
      </div>
    </div>
  );
}

export default Footer;
