import React from "react";
import { useSelector } from "react-redux";
import { motion } from 'framer-motion';
import styles from "./Projects.module.css";
import PatitasSinHogarFoto from '../../Assets/PatitasSinHogarFoto.png';
import PiDogsHenry from '../../Assets/PiDogsHenry.png';

function Projects() {
  const idioma = useSelector((state) => state.idioma);
  const temaClaro = useSelector((state) => state.temaClaro);

  const proyectos = [
    {
      titulo: idioma === 'ES' ? "Full Stack Web Developer - Henry Dogs PI" : "Full Stack Web Developer - Henry Dogs PI",
      fecha: "Jun. 2023 - Jul. 2023",
      descripcion: idioma === 'ES' ? "Desarrollé una aplicación que utiliza una API para proporcionar a los usuarios información detallada sobre diversas razas de perros..." : "I developed an application that uses an API to provide users with detailed information about various dog breeds...",
      tecnologias: ["JavaScript", "React", "Redux", "HTML", "CSS (Frontend)", "Express", "PostgreSQL", "Sequelize (Backend)"],
      repositorio: "https://github.com/JaviZaldu/ProyectoIndHenryDogs"
    },
    {
      titulo: idioma === 'ES' ? "Equipo Front-End - Patitas sin Hogar" : "Front-End Team - Homeless Paws",
      fecha: "Ago. 2023 - Sep. 2023",
      descripcion: idioma === 'ES' ? "Patitas sin Hogar es una aplicación web donde hacemos que sea muy sencillo encontrar y adoptar mascotas que necesitan un hogar..." : "Homeless Paws is a web application where we make it very easy to find and adopt pets in need of a home...",
      tecnologias: ["JavaScript", "React", "Redux", "HTML", "CSS (Frontend)", "Express", "NodeJS (Backend)", "PostgreSQL", "Sequelize (Base de Datos)"],
      repositorio: "https://github.com/RieraAndres/Henry_PF",
      deploy: "https://henry-pf-git-main-rieraandres.vercel.app/"
    }
  ];

  return (
    <div id="proyectos" className={`${styles.containerLight} ${temaClaro ? '' : styles.containerDark}`}>
      <motion.div className={styles.dataContainer}>
        <div className={`${styles.title} ${temaClaro ? '' : styles.titleDark}`}>
          {idioma === 'ES' && <h2>PROYECTOS</h2>}
          {idioma === 'EN' && <h2>PROJECTS</h2>}
        </div>
        <div className={styles.proyectos}>
            <div>
                <img className={styles.ImgProject} src={PatitasSinHogarFoto} alt="ImgProject" />
            </div> 
        </div>
      </motion.div>
    </div>
  );
}

export default Projects;
